import React, { useState } from 'react';
import { Upload, Link, FileDown, Users } from 'lucide-react';

function App() {
  const [currentDay, setCurrentDay] = useState('Mandag');
  const [loading, setLoading] = useState(false);
  const [recipeUrl, setRecipeUrl] = useState('');
  const [days, setDays] = useState({
    Mandag: { adults: 0, kids: 0, ingredients: [], servings: 0 },
    Tirsdag: { adults: 0, kids: 0, ingredients: [], servings: 0 },
    Onsdag: { adults: 0, kids: 0, ingredients: [], servings: 0 },
    Torsdag: { adults: 0, kids: 0, ingredients: [], servings: 0 },
    Fredag: { adults: 0, kids: 0, ingredients: [], servings: 0 }
  });

  const calculateTotalPeople = (adults, kids) => {
    return adults + (kids / 2);
  };

  const isCurrentDayValid = () => {
    const currentDayData = days[currentDay];
    return (
      currentDayData.servings > 0 && 
      (currentDayData.adults > 0 || currentDayData.kids > 0)
    );
  };

  const scaleIngredient = (ingredient, adults, kids, originalServings) => {
    const targetServings = adults + (kids * 0.5);
    const scalingFactor = targetServings / originalServings;
    const originalAmount = parseFloat(ingredient.amount);
    const scaledAmount = (originalAmount * scalingFactor).toFixed(1);
    return {
      ...ingredient,
      originalAmount: `${ingredient.amount}${ingredient.unit}`,
      scaledAmount: `${scaledAmount}${ingredient.unit}`
    };
  };

  const handleFileChange = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    if (!file.type.startsWith('image/')) {
      alert('Venligst vÃ¦lg en billedfil (PNG, JPG)');
      return;
    }

    setLoading(true);
    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await fetch('http://localhost:8000/parse-image', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();

      if (data.success) {
        setDays(prev => ({
          ...prev,
          [currentDay]: {
            ...prev[currentDay],
            ingredients: data.ingredients
          }
        }));
      } else {
        throw new Error(data.error || 'Failed to process image');
      }
    } catch (error) {
      console.error('Fejl ved behandling af fil:', error);
      alert('Der opstod en fejl under behandling af filen');
    } finally {
      setLoading(false);
    }
  };

  const handleUrlSubmit = async (e) => {
    e.preventDefault();
    if (!recipeUrl.trim()) return;

    setLoading(true);
    try {
      const response = await fetch(`http://localhost:8000/parse-url?url=${encodeURIComponent(recipeUrl)}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();

      if (data.success) {
        setDays(prev => ({
          ...prev,
          [currentDay]: {
            ...prev[currentDay],
            ingredients: data.ingredients
          }
        }));
        setRecipeUrl('');
      } else {
        throw new Error(data.error || 'Failed to process URL');
      }
    } catch (error) {
      console.error('Fejl ved behandling af URL:', error);
      alert('Der opstod en fejl under behandling af URLen');
    } finally {
      setLoading(false);
    }
  };

  const updatePeople = (day, type, value) => {
    setDays(prev => ({
      ...prev,
      [day]: { 
        ...prev[day], 
        [type]: parseInt(value) || 0 
      }
    }));
  };

  const updateServings = (day, value) => {
    setDays(prev => ({
      ...prev,
      [day]: {
        ...prev[day],
        servings: parseInt(value) || 0
      }
    }));
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-6xl mx-auto px-4">
        <h1 className="text-3xl font-bold text-gray-900 mb-8">Opskrift Beregner</h1>

        {/* Section 1: Daily Recipe Input */}
        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 mb-8">
          <h2 className="text-xl font-semibold text-gray-900 mb-6">Daglige Opskrifter</h2>

          {/* Day selector */}
          <div className="flex space-x-2 mb-6 overflow-x-auto pb-2">
            {Object.keys(days).map(day => (
              <button
                key={day}
                onClick={() => setCurrentDay(day)}
                className={`px-4 py-2 rounded-lg font-medium transition-colors
                  ${currentDay === day 
                    ? 'bg-blue-500 text-white' 
                    : 'bg-white text-gray-600 hover:bg-gray-100 border border-gray-200'
                  }`}
              >
                {day}
              </button>
            ))}
          </div>

          {/* People Input */}
          <div className="mb-6 p-4 bg-blue-50 rounded-lg">
            <div className="flex items-center gap-3 mb-4">
              <Users className="text-blue-500" />
              <span className="text-sm font-medium text-gray-700">Antal personer for {currentDay}:</span>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div className="flex items-center gap-3">
                <label className="text-sm font-medium text-gray-600">Original opskrift antal:</label>
                <input
                  type="number"
                  min="1"
                  value={days[currentDay].servings || ''}
                  onChange={(e) => updateServings(currentDay, e.target.value)}
                  className="w-24 px-3 py-1 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="Antal"
                />
              </div>
              <div className="flex items-center gap-3">
                <label className="text-sm font-medium text-gray-600">Voksne:</label>
                <input
                  type="number"
                  min="0"
                  value={days[currentDay].adults || ''}
                  onChange={(e) => updatePeople(currentDay, 'adults', e.target.value)}
                  className="w-24 px-3 py-1 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
              <div className="flex items-center gap-3">
                <label className="text-sm font-medium text-gray-600">BÃ¸rn:</label>
                <input
                  type="number"
                  min="0"
                  value={days[currentDay].kids || ''}
                  onChange={(e) => updatePeople(currentDay, 'kids', e.target.value)}
                  className="w-24 px-3 py-1 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
            </div>
            <div className="mt-3 text-sm text-gray-600">
              Samlet antal: {calculateTotalPeople(days[currentDay].adults, days[currentDay].kids)} personer
            </div>
          </div>

          {/* Recipe Input Methods */}
          <div className="grid md:grid-cols-2 gap-6">
            {/* URL Input */}
            <div>
              <h3 className="text-lg font-semibold text-gray-900 mb-3">TilfÃ¸j Opskrift URL</h3>
              <form onSubmit={handleUrlSubmit} className="flex gap-2">
                <input
                  type="url"
                  value={recipeUrl}
                  onChange={(e) => setRecipeUrl(e.target.value)}
                  placeholder="IndsÃ¦t opskrift URL her"
                  className="flex-grow px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  disabled={loading || !isCurrentDayValid()}
                />
                <button
                  type="submit"
                  disabled={loading || !isCurrentDayValid()}
                  className="flex items-center gap-2 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 disabled:bg-gray-400 disabled:cursor-not-allowed transition-colors"
                >
                  <Link className="w-5 h-5" />
                  {loading ? 'Behandler...' : 'ImportÃ©r'}
                </button>
              </form>
              {!isCurrentDayValid() && (
                <div className="text-sm text-amber-600 mt-2">
                  Udfyld venligst antal personer og portioner fÃ¸r du tilfÃ¸jer ingredienser
                </div>
              )}
            </div>

            {/* File Upload */}
            <div>
              <h3 className="text-lg font-semibold text-gray-900 mb-3">Upload Opskrift Billede</h3>
              <div className="border-2 border-dashed border-gray-300 rounded-lg p-6">
                <label className={`flex flex-col items-center ${loading || !isCurrentDayValid() ? 'cursor-not-allowed' : 'cursor-pointer'}`}>
                  <Upload className="w-8 h-8 text-gray-400 mb-2" />
                  <span className="text-sm text-gray-600">
                    {loading ? 'Behandler...' : 'Klik for at uploade eller trÃ¦k filen hertil'}
                  </span>
                  <span className="text-xs text-gray-500 mt-1">PNG, JPG op til 10MB</span>
                  <input
                    type="file"
                    onChange={handleFileChange}
                    accept="image/*"
                    className="hidden"
                    disabled={loading || !isCurrentDayValid()}
                  />
                </label>
              </div>
              {!isCurrentDayValid() && (
                <div className="text-sm text-amber-600 mt-2">
                  Udfyld venligst antal personer og portioner fÃ¸r du tilfÃ¸jer ingredienser
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Section 2: Total Ingredients List */}
        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 mb-6">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-xl font-semibold text-gray-900">Samlet IndkÃ¸bsliste</h2>
            <button
              className="flex items-center gap-2 px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600 transition-colors"
              onClick={() => console.log('Export clicked')}
              disabled={loading}
            >
              <FileDown className="w-5 h-5" />
              EksportÃ©r til Excel
            </button>
          </div>

          <div className="overflow-x-auto">
            <table className="w-full">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-4 py-3 text-left text-sm font-semibold text-gray-900">Ingrediens</th>
                  {Object.keys(days).map(day => (
                    <th key={day} className="px-4 py-3 text-left text-sm font-semibold text-gray-900">
                      {day}<br/>
                      <span className="font-normal text-gray-500">
                        {calculateTotalPeople(days[day].adults, days[day].kids)} personer
                      </span>
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                {Object.values(days)
                  .flatMap(day => day.ingredients)
                  .filter((ingredient, index, self) => 
                    index === self.findIndex(i => i.name === ingredient.name)
                  )
                  .map((ingredient) => (
                    <tr key={ingredient.name}>
                      <td className="px-4 py-3 text-sm text-gray-900">
                        {ingredient.name}
                      </td>
                      {Object.entries(days).map(([dayName, dayData]) => {
                        const dayIngredient = dayData.ingredients.find(
                          i => i.name === ingredient.name
                        );

                        if (!dayIngredient) {
                          return (
                            <td key={dayName} className="px-4 py-3 text-sm text-gray-400">
                              -
                            </td>
                          );
                        }

                        const scaled = scaleIngredient(
                          dayIngredient, 
                          dayData.adults, 
                          dayData.kids,
                          dayData.servings
                        );

                        return (
                          <td key={dayName} className="px-4 py-3">
                            <div className="text-sm text-gray-900">
                              {scaled.scaledAmount}
                            </div>
                            <div className="text-xs text-gray-500">
                              (original: {scaled.originalAmount} til {dayData.servings} personer)
                            </div>
                          </td>
                        );
                      })}
                    </tr>
                  ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
