import React, { useState } from 'react';
import { Upload, Link, FileDown, Users } from 'lucide-react';

// Constants
const WEEKDAYS = ['Mandag', 'Tirsdag', 'Onsdag', 'Torsdag', 'Fredag'];

const FOOD_CATEGORIES = {
  'Kød/Fisk': {
    keywords: ['kød', 'bøf', 'bacon', 'laks', 'kotelet', 'reje', 'hummer', 'okse', 'kylling', 'filet', 'bisk'],
  },
  'Mejeri': {
    keywords: ['mælk', 'smør', 'æg', 'cremefraiche', 'fløde', 'yoghurt', 'ost', 'frossen spinat'],
  },
  'Kolonial': {
    keywords: ['mel', 'stivelse', 'stødt', 'tørret', 'krydder', 'kerner', 'mandler', 'nødder', 'pure', 'hakket tomat', 'plader', 'bouillon', 'vin', 'olie'],
  },
  'Grønt': {
    keywords: ['kartoffel', 'løg', 'hvidløg', 'salat', 'tomat', 'selleri', 'champignon', 'squash', 'aubergine', 'gulerod', 'persille', 'basilikum', 'dild', 'æble', 'citron', 'granatæble', 'pinjekerner'],
  }
};

// Helper Components
const DaySelector = ({ currentDay, setCurrentDay }) => (
  <div className="flex space-x-2 mb-6 overflow-x-auto pb-2">
    {WEEKDAYS.map(day => (
      <button
        key={day}
        onClick={() => setCurrentDay(day)}
        className={`px-4 py-2 rounded-lg font-medium transition-colors
          ${currentDay === day 
            ? 'bg-zinc-900 text-white' 
            : 'bg-white text-zinc-900 hover:bg-zinc-100 border border-zinc-200'}`}
      >
        {day}
      </button>
    ))}
  </div>
);

const InputField = ({ label, value, onChange }) => (
  <div className="flex items-center gap-3">
    <label className="text-sm font-medium text-zinc-600">{label}</label>
    <input
      type="number"
      min="0"
      value={value || ''}
      onChange={onChange}
      className="w-24 px-3 py-1 border border-zinc-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-zinc-900"
    />
  </div>
);

const PeopleInput = ({ day, dayData, onUpdatePeople, onUpdateServings }) => (
  <div className="mb-6 p-4 bg-zinc-50 rounded-lg border border-zinc-200">
    <div className="flex items-center gap-3 mb-4">
      <Users className="text-zinc-900" />
      <span className="text-sm font-medium text-zinc-900">Antal personer for {day}:</span>
    </div>
    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
      <InputField
        label="Original opskrift antal:"
        value={dayData.servings}
        onChange={(e) => onUpdateServings(day, e.target.value)}
      />
      <InputField
        label="Voksne:"
        value={dayData.adults}
        onChange={(e) => onUpdatePeople(day, 'adults', e.target.value)}
      />
      <InputField
        label="Børn:"
        value={dayData.kids}
        onChange={(e) => onUpdatePeople(day, 'kids', e.target.value)}
      />
    </div>
    <div className="mt-3 text-sm text-zinc-500">
      Samlet antal: {calculateTotalPeople(dayData.adults, dayData.kids)} personer
    </div>
  </div>
);

const RecipeInput = ({ isValid, onUrlSubmit, recipeUrl, setRecipeUrl, onFileChange, loading }) => (
  <div className="grid md:grid-cols-2 gap-6">
    <div>
      <h3 className="text-lg font-semibold text-zinc-900 mb-3">Tilføj Opskrift URL</h3>
      <form onSubmit={onUrlSubmit} className="flex gap-2">
        <input
          type="url"
          value={recipeUrl}
          onChange={(e) => setRecipeUrl(e.target.value)}
          placeholder="Indsæt opskrift URL her"
          className="flex-grow px-4 py-2 border border-zinc-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-zinc-900"
          disabled={loading || !isValid}
        />
        <button
          type="submit"
          disabled={loading || !isValid}
          className="flex items-center gap-2 px-4 py-2 bg-zinc-900 text-white rounded-lg hover:bg-zinc-800 disabled:bg-zinc-300 disabled:cursor-not-allowed transition-colors"
        >
          <Link className="w-5 h-5" />
          {loading ? 'Behandler...' : 'Importér'}
        </button>
      </form>
      {!isValid && (
        <div className="text-sm text-amber-600 mt-2">
          Udfyld venligst antal personer og portioner før du tilføjer ingredienser
        </div>
      )}
    </div>

    <div>
      <h3 className="text-lg font-semibold text-zinc-900 mb-3">Upload Opskrift Billede</h3>
      <div className="border-2 border-dashed border-zinc-200 rounded-lg p-6">
        <label className={`flex flex-col items-center ${loading || !isValid ? 'cursor-not-allowed' : 'cursor-pointer'}`}>
          <Upload className="w-8 h-8 text-zinc-400 mb-2" />
          <span className="text-sm text-zinc-600">
            {loading ? 'Behandler...' : 'Klik for at uploade eller træk filen hertil'}
          </span>
          <span className="text-xs text-zinc-500 mt-1">PNG, JPG op til 10MB</span>
          <input
            type="file"
            onChange={onFileChange}
            accept="image/*"
            className="hidden"
            disabled={loading || !isValid}
          />
        </label>
      </div>
    </div>
  </div>
);

// Main App Component
const App = () => {
  const [currentDay, setCurrentDay] = useState(WEEKDAYS[0]);
  const [loading, setLoading] = useState(false);
  const [recipeUrl, setRecipeUrl] = useState('');
  const [days, setDays] = useState(
    WEEKDAYS.reduce((acc, day) => ({
      ...acc,
      [day]: { adults: 0, kids: 0, ingredients: [], servings: 0 }
    }), {})
  );

  const updatePeople = (day, type, value) => {
    setDays(prev => ({
      ...prev,
      [day]: { ...prev[day], [type]: parseInt(value) || 0 }
    }));
  };

  const updateServings = (day, value) => {
    setDays(prev => ({
      ...prev,
      [day]: { ...prev[day], servings: parseInt(value) || 0 }
    }));
  };

  const updateIngredients = (day, ingredients) => {
    setDays(prev => ({
      ...prev,
      [day]: { ...prev[day], ingredients }
    }));
  };

  const calculateTotalPeople = (adults, kids) => {
    return adults + (kids * 0.5);
  };

  const isCurrentDayValid = () => {
    const currentDayData = days[currentDay];
    return currentDayData.servings > 0 && (currentDayData.adults > 0 || currentDayData.kids > 0);
  };

  const handleFileChange = async (event) => {
    // Your existing file change handler
  };

  const handleUrlSubmit = async (e) => {
    // Your existing URL submit handler
  };

  return (
    <div className="min-h-screen bg-zinc-50 py-8">
      <div className="max-w-6xl mx-auto px-4">
        <h1 className="text-3xl font-bold text-zinc-900 mb-8">Opskrift Beregner</h1>

        <div className="bg-white rounded-xl shadow-sm border border-zinc-200 p-6 mb-8">
          <h2 className="text-xl font-semibold text-zinc-900 mb-6">Daglige Opskrifter</h2>

          <DaySelector currentDay={currentDay} setCurrentDay={setCurrentDay} />

          <PeopleInput
            day={currentDay}
            dayData={days[currentDay]}
            onUpdatePeople={updatePeople}
            onUpdateServings={updateServings}
          />

          <RecipeInput
            isValid={isCurrentDayValid()}
            onUrlSubmit={handleUrlSubmit}
            recipeUrl={recipeUrl}
            setRecipeUrl={setRecipeUrl}
            onFileChange={handleFileChange}
            loading={loading}
          />
        </div>

        {/* Ingredients section would go here */}
      </div>
    </div>
  );
};

export default App;
