import React, { useState } from 'react';
import { Upload, Link, FileDown, Users } from 'lucide-react';
import { Card, CardHeader, CardTitle, CardContent } from '@/components/ui/card';

// Move constants outside component
const DAYS = ['Mandag', 'Tirsdag', 'Onsdag', 'Torsdag', 'Fredag'];
const CATEGORIES = ['Kød/Fisk', 'Mejeri', 'Grønt', 'Kolonial', 'Ukategoriseret'];

const FOOD_CATEGORIES = {
  'Kød/Fisk': {
    keywords: ['kød', 'bøf', 'bacon', 'laks', 'kotelet', 'reje', 'hummer', 'okse', 'kylling', 'filet', 'bisk'],
  },
  'Mejeri': {
    keywords: ['mælk', 'smør', 'æg', 'cremefraiche', 'fløde', 'yoghurt', 'ost', 'frossen spinat'],
  },
  'Kolonial': {
    keywords: ['mel', 'stivelse', 'stødt', 'tørret', 'krydder', 'kerner', 'mandler', 'nødder', 'pure', 'hakket tomat', 'plader', 'bouillon', 'vin', 'olie'],
  },
  'Grønt': {
    keywords: ['kartoffel', 'løg', 'hvidløg', 'salat', 'tomat', 'selleri', 'champignon', 'squash', 'aubergine', 'gulerod', 'persille', 'basilikum', 'dild', 'æble', 'citron', 'granatæble', 'pinjekerner'],
  }
};

// Separate components for better organization
const DaySelector = ({ currentDay, setCurrentDay }) => (
  <div className="flex space-x-2 mb-6 overflow-x-auto pb-2">
    {DAYS.map(day => (
      <button
        key={day}
        onClick={() => setCurrentDay(day)}
        className={`px-4 py-2 rounded-lg font-medium transition-colors
          ${currentDay === day 
            ? 'bg-blue-500 text-white' 
            : 'bg-white text-gray-600 hover:bg-gray-100 border border-gray-200'}`}
      >
        {day}
      </button>
    ))}
  </div>
);

const PeopleInput = ({ day, dayData, updatePeople, updateServings, calculateTotalPeople }) => (
  <div className="mb-6 p-4 bg-blue-50 rounded-lg">
    <div className="flex items-center gap-3 mb-4">
      <Users className="text-blue-500" />
      <span className="text-sm font-medium text-gray-700">Antal personer for {day}:</span>
    </div>
    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
      <InputField
        label="Original opskrift antal:"
        value={dayData.servings}
        onChange={(value) => updateServings(day, value)}
      />
      <InputField
        label="Voksne:"
        value={dayData.adults}
        onChange={(value) => updatePeople(day, 'adults', value)}
      />
      <InputField
        label="Børn:"
        value={dayData.kids}
        onChange={(value) => updatePeople(day, 'kids', value)}
      />
    </div>
    <div className="mt-3 text-sm text-gray-600">
      Samlet antal: {calculateTotalPeople(dayData.adults, dayData.kids)} personer
    </div>
  </div>
);

const InputField = ({ label, value, onChange }) => (
  <div className="flex items-center gap-3">
    <label className="text-sm font-medium text-gray-600">{label}</label>
    <input
      type="number"
      min="0"
      value={value || ''}
      onChange={(e) => onChange(e.target.value)}
      className="w-24 px-3 py-1 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
    />
  </div>
);

const IngredientsTable = ({ category, ingredients, days, calculateTotalPeople, scaleIngredient, updateIngredientCategory }) => (
  <div className="mb-8">
    <h3 className={`text-lg font-semibold mb-4 ${
      category === 'Ukategoriseret' ? 'text-amber-600' : 'text-gray-900'
    }`}>
      {category}
    </h3>

    <table className="w-full">
      <thead className="bg-gray-50">
        <tr>
          <th className="px-4 py-3 text-left text-sm font-semibold text-gray-900">
            Ingrediens
            {category === 'Ukategoriseret' && (
              <span className="block text-xs font-normal text-amber-600">
                Vælg korrekt kategori
              </span>
            )}
          </th>
          {DAYS.map(day => (
            <th key={day} className="px-4 py-3 text-left text-sm font-semibold text-gray-900">
              {day}<br/>
              <span className="font-normal text-gray-500">
                {calculateTotalPeople(days[day].adults, days[day].kids)} personer
              </span>
            </th>
          ))}
          {category === 'Ukategoriseret' && (
            <th className="px-4 py-3 text-left text-sm font-semibold text-gray-900">
              Kategori
            </th>
          )}
        </tr>
      </thead>
      <tbody className="divide-y divide-gray-200">
        {ingredients.map((ingredient) => (
          <tr key={ingredient.name}>
            <td className="px-4 py-3 text-sm text-gray-900">
              {ingredient.name}
            </td>
            {Object.entries(days).map(([dayName, dayData]) => {
              const dayIngredient = dayData.ingredients.find(i => i.name === ingredient.name);
              if (!dayIngredient) {
                return <td key={dayName} className="px-4 py-3 text-sm text-gray-400">-</td>;
              }

              const scaled = scaleIngredient(dayIngredient, dayData.adults, dayData.kids, dayData.servings);
              return (
                <td key={dayName} className="px-4 py-3">
                  <div className="text-sm text-gray-900">{scaled.scaledAmount}</div>
                  <div className="text-xs text-gray-500">
                    (original: {scaled.originalAmount} til {dayData.servings} personer)
                  </div>
                </td>
              );
            })}
            {category === 'Ukategoriseret' && (
              <td className="px-4 py-3">
                <select
                  className="text-sm border rounded p-1"
                  onChange={(e) => updateIngredientCategory(ingredient.name, e.target.value)}
                  defaultValue=""
                >
                  <option value="" disabled>Vælg kategori</option>
                  {CATEGORIES.slice(0, -1).map(cat => (
                    <option key={cat} value={cat}>{cat}</option>
                  ))}
                </select>
              </td>
            )}
          </tr>
        ))}
      </tbody>
    </table>
  </div>
);

function App() {
  const [currentDay, setCurrentDay] = useState(DAYS[0]);
  const [loading, setLoading] = useState(false);
  const [recipeUrl, setRecipeUrl] = useState('');
  const [days, setDays] = useState(
    Object.fromEntries(
      DAYS.map(day => [day, { adults: 0, kids: 0, ingredients: [], servings: 0 }])
    )
  );

  const categorizeIngredient = (name) => {
    const normalizedName = name.toLowerCase().trim();
    return Object.entries(FOOD_CATEGORIES).find(
      ([_, data]) => data.keywords.some(word => normalizedName.includes(word))
    )?.[0] || 'Ukategoriseret';
  };

  const calculateTotalPeople = (adults, kids) => adults + (kids / 2);

  const isCurrentDayValid = () => {
    const { servings, adults, kids } = days[currentDay];
    return servings > 0 && (adults > 0 || kids > 0);
  };

  const scaleIngredient = (ingredient, adults, kids, originalServings) => {
    const scalingFactor = calculateTotalPeople(adults, kids) / originalServings;
    const scaledAmount = (parseFloat(ingredient.amount) * scalingFactor).toFixed(1);
    return {
      ...ingredient,
      originalAmount: `${ingredient.amount}${ingredient.unit}`,
      scaledAmount: `${scaledAmount}${ingredient.unit}`
    };
  };

  const handleFileUpload = async (file) => {
    if (!file || !file.type.startsWith('image/')) {
      alert('Venligst vælg en billedfil (PNG, JPG)');
      return;
    }

    setLoading(true);
    try {
      const formData = new FormData();
      formData.append('file', file);
      const response = await fetch('http://localhost:8000/parse-image', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      if (!response.ok) throw new Error(data.error || 'Failed to process image');

      if (data.success) {
        setDays(prev => ({
          ...prev,
          [currentDay]: { ...prev[currentDay], ingredients: data.ingredients }
        }));
      }
    } catch (error) {
      console.error('Fejl ved behandling af fil:', error);
      alert('Der opstod en fejl under behandling af filen');
    } finally {
      setLoading(false);
    }
  };

  const handleUrlSubmit = async (e) => {
    e.preventDefault();
    if (!recipeUrl.trim()) return;

    setLoading(true);
    try {
      const response = await fetch(
        `http://localhost:8000/parse-url?url=${encodeURIComponent(recipeUrl)}`,
        { method: 'POST', headers: { 'Content-Type': 'application/json' } }
      );

      const data = await response.json();
      if (!response.ok) throw new Error(data.error || 'Failed to process URL');

      if (data.success) {
        setDays(prev => ({
          ...prev,
          [currentDay]: { ...prev[currentDay], ingredients: data.ingredients }
        }));
        setRecipeUrl('');
      }
    } catch (error) {
      console.error('Fejl ved behandling af URL:', error);
      alert('Der opstod en fejl under behandling af URLen');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-6xl mx-auto px-4">
        <h1 className="text-3xl font-bold text-gray-900 mb-8">Opskrift Beregner</h1>

        <Card className="mb-8">
          <CardHeader>
            <CardTitle>Daglige Opskrifter</CardTitle>
          </CardHeader>
          <CardContent>
            <DaySelector currentDay={currentDay} setCurrentDay={setCurrentDay} />

            <PeopleInput
              day={currentDay}
              dayData={days[currentDay]}
              updatePeople={(day, type, value) => setDays(prev => ({
                ...prev,
                [day]: { ...prev[day], [type]: parseInt(value) || 0 }
              }))}
              updateServings={(day, value) => setDays(prev => ({
                ...prev,
                [day]: { ...prev[day], servings: parseInt(value) || 0 }
              }))}
              calculateTotalPeople={calculateTotalPeople}
            />

            <div className="grid md:grid-cols-2 gap-6">
              {/* URL Input Form */}
              <div>
                <h3 className="text-lg font-semibold text-gray-900 mb-3">Tilføj Opskrift URL</h3>
                <form onSubmit={handleUrlSubmit} className="flex gap-2">
                  <input
                    type="url"
                    value={recipeUrl}
                    onChange={(e) => setRecipeUrl(e.target.value)}
                    placeholder="Indsæt opskrift URL her"
                    className="flex-grow px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                    disabled={loading || !isCurrentDayValid()}
                  />
                  <button
                    type="submit"
                    disabled={loading || !isCurrentDayValid()}
                    className="flex items-center gap-2 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 disabled:bg-gray-400 disabled:cursor-not-allowed transition-colors"
                  >
                    <Link className="w-5 h-5" />
                    {loading ? 'Behandler...' : 'Importér'}
                  </button>
                </form>
                {!isCurrentDayValid() && (
                  <div className="text-sm text-amber-600 mt-2">
                    Udfyld venligst antal personer og portioner før du tilføjer ingredienser
                  </div>
                )}
              </div>

              {/* File Upload */}
              <div>
                <h3 className="text-lg font-semibold text-gray-900 mb-3">Upload Opskrift Billede</h3>
                <div className="border-2 border-dashed border-gray-300 rounded-lg p-6">
                  <label className={`flex flex-col items-center ${loading || !isCurrentDayValid() ? 'cursor-not-allowed' : 'cursor-pointer'}`}>
                    <Upload className="w-8 h-8 text-gray-400 mb-2" />
                    <span className="text-sm text-gray-600">
                      {loading ? 'Behandler...' : 'Klik for at uploade eller træk filen hertil'}
                    </span>
                    <span className="text-xs text-gray-500 mt-1">PNG, JPG op til 10MB</span>
                    <input
                      type="file"
                      onChange={(e) => handleFileUpload(e.target.files[0])}
                      accept="image/*"
                      className="hidden"
                      disabled={loading || !isCurrentDayValid()}
                    />
                  </label>
                </div>
                {!isCurrentDayValid() && (
                  <div className="text-sm text-amber-600 mt-2">
                    Udfyld venligst antal personer og portioner før du tilføjer ingredienser
                  </div>
                )}
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Samlet Indkøbsliste</CardTitle>
          </CardHeader>
          <CardContent>
            {CATEGORIES.map(category => {
              const categoryIngredients = Object.values(days)
                .flatMap(day => day.ingredients)
                .filter((ingredient, index, self) => 
                  index === self.findIndex(i => i.name === ingredient.name) &&
                  categorizeIngredient(ingredient.name) === category
                );

              if (categoryIngredients.length === 0) return null;

              return (
                <IngredientsTable
                  key={category}
                  category={category}
                  ingredients={categoryIngredients}
                  days={days}
                  calculateTotalPeople={calculateTotalPeople}
                  scaleIngredient={scaleIngredient}
                  updateIngredientCategory={(ingredientName, newCategory) => {
                    setDays(prev => {
                      const newDays = { ...prev };
                      Object.keys(newDays).forEach(day => {
                        newDays[day].ingredients = newDays[day].ingredients.map(ing => {
                          if (ing.name === ingredientName) {
                            return { ...ing, category: newCategory };
                          }
                          return ing;
                        });
                      });
                      return newDays;
                    });
                  }}
                />
              );
            })}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

export default App;
