import React, { useState } from 'react';
import { Upload, Link, FileDown, Users } from 'lucide-react';

const App = () => {
  // State definitions
  const [days, setDays] = useState({
    Mandag: { servings: 4, adults: 2, kids: 2, ingredients: [] },
    Tirsdag: { servings: 4, adults: 2, kids: 2, ingredients: [] },
    Onsdag: { servings: 4, adults: 2, kids: 2, ingredients: [] },
    Torsdag: { servings: 4, adults: 2, kids: 2, ingredients: [] },
    Fredag: { servings: 4, adults: 2, kids: 2, ingredients: [] }
  });
  const [currentDay, setCurrentDay] = useState('Mandag');
  const [recipeUrl, setRecipeUrl] = useState('');
  const [loading, setLoading] = useState(false);

  // Helper functions
  const calculateTotalPeople = (adults, kids) => {
    return (parseInt(adults) || 0) + (parseInt(kids) || 0);
  };

  const updateServings = (day, value) => {
    setDays(prev => ({
      ...prev,
      [day]: { ...prev[day], servings: parseInt(value) || 0 }
    }));
  };

  const updatePeople = (day, type, value) => {
    setDays(prev => ({
      ...prev,
      [day]: { ...prev[day], [type]: parseInt(value) || 0 }
    }));
  };

  const handleUrlSubmit = (e) => {
    e.preventDefault();
    // URL submission logic here
  };

  const handleFileChange = (e) => {
    // File change logic here
  };

  const isCurrentDayValid = () => {
    const dayData = days[currentDay];
    return dayData && dayData.servings > 0 && (dayData.adults > 0 || dayData.kids > 0);
  };

  const scaleIngredient = (ingredient, adults, kids, servings) => {
    const totalPeople = calculateTotalPeople(adults, kids);
    const scaleFactor = totalPeople / servings;
    const scaledAmount = `${(parseFloat(ingredient.amount) * scaleFactor).toFixed(1)} ${ingredient.unit}`;
    const originalAmount = `${ingredient.amount} ${ingredient.unit}`;
    return { scaledAmount, originalAmount };
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-zinc-50 to-zinc-100 py-12">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <h1 className="text-4xl font-bold text-zinc-900 mb-2">Opskrift Beregner</h1>
        <p className="text-zinc-600 mb-8">Beregn ingredienser baseret p√• antal personer</p>

        <div className="bg-white rounded-2xl shadow-lg border border-zinc-100 p-8 mb-10">
          <h2 className="text-2xl font-semibold text-zinc-900 mb-8">Daglige Opskrifter</h2>

          <div className="flex space-x-3 mb-8 overflow-x-auto pb-2 scrollbar-hide">
            {Object.keys(days).map(day => (
              <button
                key={day}
                onClick={() => setCurrentDay(day)}
                className={`px-6 py-3 rounded-xl font-medium transition-all duration-200 ${
                  currentDay === day 
                    ? 'bg-zinc-900 text-white shadow-md transform scale-105' 
                    : 'bg-white text-zinc-700 hover:bg-zinc-50 border border-zinc-200 hover:border-zinc-300'
                }`}
              >
                {day}
              </button>
            ))}
          </div>

          <div className="mb-8 bg-zinc-50 rounded-xl p-6">
            <div className="flex items-center gap-3 mb-6">
              <div className="p-2 bg-white rounded-lg shadow-sm">
                <Users className="text-zinc-700 w-5 h-5" />
              </div>
              <span className="text-base font-medium text-zinc-800">Antal personer for {currentDay}</span>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {['servings', 'adults', 'kids'].map(type => (
                <div key={type} className="flex flex-col gap-2">
                  <label className="text-sm font-medium text-zinc-700">
                    {type === 'servings' ? 'Original opskrift antal:' : type === 'adults' ? 'Voksne:' : 'B√∏rn:'}
                  </label>
                  <input
                    type="number"
                    min="0"
                    value={days[currentDay][type] || ''}
                    onChange={(e) => type === 'servings' ? updateServings(currentDay, e.target.value) : updatePeople(currentDay, type, e.target.value)}
