import React, { useState } from 'react';
import { Upload, Link, FileDown, Users } from 'lucide-react';

// List of common preparation terms to remove
const preparationTerms = [
  'finthakkede', 'fintrevet', 'pressede', 'hakket', 'skiver', 'finsnittet', 'smuldret', 'kogte',
  'delte', 'opskåret', 'røget', 'skårne', 'kompakte', 'håndfulde', 'skrællede', 'ristede', 'sautéede',
  'opvarmede', 'grillede', 'indbagt', 'stegte', 'blancherede', 'skært', 'skåret i strimler', 'dl', 'fint', 
  'groftrevet', 'skåret i kvarte', 'skåret i halve', 'roftrevet', 'fintsnittet',
];

// Regular expression pattern to match and remove preparation terms
const prepRegex = new RegExp(`(${preparationTerms.join('|')})`, 'gi');

// Normalize ingredient name by removing preparation terms
const normalizeIngredient = (ingredientName) => {
  const normalizedName = ingredientName.toLowerCase().trim();
  return normalizedName.replace(prepRegex, '').trim(); // Remove prep terms and trim any extra spaces
};

// Define food categories with keywords for categorization
const FOOD_CATEGORIES = {
  '🥩 Kød/Fisk': ['kød', 'bøf', 'bacon', 'laks', 'kotelet', 'reje', 'hummer', 'okse', 'kylling', 'filet', 'bisk'],
  '🧀 Mejeri': ['mælk', 'smør', 'æg', 'cremefraiche', 'fløde', 'yoghurt', 'ost', 'frossen spinat'],
  '🥫 Kolonial': ['mel', 'stivelse', 'stødt', 'tørret', 'krydder', 'kerner', 'mandler', 'nødder', 'pure', 'hakket tomat', 'plader', 'bouillon', 'vin', 'olie','laurbærblade', 'sød paprika', 'paprika', 'muskatnød'],
  '🥕 Grønt': ['kartoffel', 'kartofler', 'løg', 'spidskål', 'hvidløg', 'salat', 'tomat', 'selleri', 'champignon', 'squash', 'aubergine', 'gulerod', 'persille', 'basilikum', 'dild', 'æble', 'citron', 'granatæble', 'pinjekerner']
};

// Function to initialize the days state
const createInitialDays = () => {
  const weekdays = ['Mandag', 'Tirsdag', 'Onsdag', 'Torsdag', 'Fredag'];
  return weekdays.reduce((acc, day) => ({
    ...acc,
    [day]: { adults: 0, kids: 0, ingredients: [], servings: 0 }
  }), {});
};

const App = () => {
  const [currentDay, setCurrentDay] = useState('Mandag');
  const [loading, setLoading] = useState(false);
  const [recipeUrl, setRecipeUrl] = useState('');
  const [days, setDays] = useState(createInitialDays());

  const handleFileChange = async (event) => {
    const file = event.target.files[0];
    if (!file || !file.type.startsWith('image/')) {
      alert('Venligst vælg en billedfil (PNG, JPG)');
      return;
    }
    setLoading(true);
    try {
      const formData = new FormData();
      formData.append('file', file);
      const response = await fetch('http://localhost:8000/parse-image', {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();
      if (data.success) {
        updateIngredients(data.ingredients);
      } else {
        throw new Error(data.error || 'Failed to process image');
      }
    } catch (error) {
      alert('Der opstod en fejl under behandling af filen');
    } finally {
      setLoading(false);
    }
  };

  const handleUrlSubmit = async (e) => {
    e.preventDefault();
    if (!recipeUrl.trim()) return;
    setLoading(true);
    try {
      const response = await fetch(`http://localhost:8000/parse-url?url=${encodeURIComponent(recipeUrl)}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
      });
      const data = await response.json();
      if (data.success) {
        updateIngredients(data.ingredients);
        setRecipeUrl('');
      } else {
        throw new Error(data.error || 'Failed to process URL');
      }
    } catch (error) {
      alert('Der opstod en fejl under behandling af URLen');
    } finally {
      setLoading(false);
    }
  };

  const updateIngredients = (ingredients) => {
    const normalizedIngredients = ingredients.map(ingredient => ({
      ...ingredient,
      name: normalizeIngredient(ingredient.name),
    }));

    setDays(prev => ({
      ...prev,
      [currentDay]: { ...prev[currentDay], ingredients: normalizedIngredients }
    }));
  };

  const updatePeople = (day, type, value) => {
    setDays(prev => ({
      ...prev,
      [day]: { ...prev[day], [type]: parseInt(value) || 0 }
    }));
  };

  const updateServings = (day, value) => {
    setDays(prev => ({
      ...prev,
      [day]: { ...prev[day], servings: parseInt(value) || 0 }
    }));
  };

  const scaleIngredient = (ingredient, adults, kids, originalServings) => {
    const targetServings = adults + (kids / 2);
    const scalingFactor = targetServings / originalServings;
    const originalAmount = parseFloat(ingredient.amount);
    const scaledAmount = (originalAmount * scalingFactor).toFixed(1);
    return { ...ingredient, originalAmount: `${ingredient.amount}${ingredient.unit}`, scaledAmount: `${scaledAmount}${ingredient.unit}` };
  };

  const calculateTotalPeople = (adults, kids) => adults + (kids / 2);

  const isCurrentDayValid = () => {
    const { servings, adults, kids } = days[currentDay];
    return servings > 0 && (adults > 0 || kids > 0);
  };

  // IngredientTable component
  const IngredientTable = ({ category }) => {
    const categorizeIngredient = (name) => {
      const normalizedName = normalizeIngredient(name);
      return Object.entries(FOOD_CATEGORIES).find(([_, keywords]) =>
        keywords.some(word => normalizedName.includes(word))
      )?.[0] || 'Ukategoriseret';
    };

    const groupedIngredients = Object.values(days)
      .flatMap(day => day.ingredients)
      .reduce((acc, ingredient) => {
        const normName = normalizeIngredient(ingredient.name);
        if (!acc[normName]) {
          acc[normName] = { ...ingredient, occurrences: 1 };
        } else {
          acc[normName].occurrences += 1;
        }
        return acc;
      }, {});

    const categoryIngredients = Object.values(groupedIngredients)
      .filter((ingredient) => categorizeIngredient(ingredient.name) === category);

    if (categoryIngredients.length === 0) return null;

    return (
      <div key={category} className="mb-8">
        <h3 className={`text-lg font-semibold mb-4 ${category === 'Ukategoriseret' ? 'text-amber-600' : 'text-gray-900'}`}>
          {category}
        </h3>
        <table className="w-full">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-4 py-3 text-left text-sm font-semibold text-gray-900">Ingrediens</th>
              {Object.keys(days).map(day => (
                <th key={day} className="px-4 py-3 text-left text-sm font-semibold text-gray-900">
                  {day}<br/>
                  <span className="font-normal text-gray-500">
                    {calculateTotalPeople(days[day].adults, days[day].kids)} personer
                  </span>
                </th>
              ))}
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-200">
            {categoryIngredients.map(ingredient => (
              <tr key={ingredient.name}>
                <td className="px-4 py-3 text-sm text-gray-900">{ingredient.name} ({ingredient.occurrences} times)</td>
                {Object.entries(days).map(([dayName, dayData]) => {
                  const dayIngredient = dayData.ingredients.find(i => i.name === ingredient.name);
                  if (!dayIngredient) return <td key={dayName} className="px-4 py-3 text-sm text-gray-400">-</td>;
                  const scaled = scaleIngredient(dayIngredient, dayData.adults, dayData.kids, dayData.servings);
                  return (
                    <td key={dayName} className="px-4 py-3">
                      <div className="text-sm text-gray-900">{scaled.scaledAmount}</div>
                      <div className="text-xs text-gray-500">(original: {scaled.originalAmount} til {dayData.servings} personer)</div>
                    </td>
                  );
                })}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-6xl mx-auto px-6">
        <h1 className="text-4xl font-bold text-gray-900 mb-8">Opskrift Beregner</h1>

        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 mb-8">
          <h2 className="text-xl font-semibold text-gray-900 mb-6">Daglige Opskrifter</h2>

          <div className="flex space-x-2 mb-6 overflow-x-auto pb-2">
            {Object.keys(days).map(day => (
              <button
                key={day}
                onClick={() => setCurrentDay(day)}
                className={`px-4 py-2 rounded-lg font-medium transition-colors ${currentDay === day ? 'bg-blue-500 text-white' : 'bg-white text-gray-600 hover:bg-gray-100 border border-gray-200'}`}
              >
                {day}
              </button>
            ))}
          </div>

          {/* People and Servings Inputs */}
          <div className="mb-6 p-4 bg-blue-50 rounded-lg">
            <div className="flex items-center gap-3 mb-4">
              <Users className="text-blue-500" />
              <span className="text-sm font-medium text-gray-700">Antal personer for {currentDay}:</span>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              {['servings', 'adults', 'kids'].map(type => (
                <div key={type} className="flex items-center gap-3">
                  <label className="text-sm font-medium text-gray-600">{type === 'servings' ? 'Original opskrift antal:' : type === 'adults' ? 'Voksne:' : 'Børn:'}</label>
                  <input
                    type="number"
                    min="0"
                    value={days[currentDay][type] || ''}
                    onChange={(e) => type === 'servings' ? updateServings(currentDay, e.target.value) : updatePeople(currentDay, type, e.target.value)}
                    className="w-24 px-3 py-1 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>
              ))}
            </div>
            <div className="mt-3 text-sm text-gray-600">
              Samlet antal: {calculateTotalPeople(days[currentDay].adults, days[currentDay].kids)} personer
            </div>
          </div>

          {/* Recipe Input Methods */}
          <div className="grid md:grid-cols-2 gap-6">
            <div>
              <h3 className="text-lg font-semibold text-gray-900 mb-3">Tilføj Opskrift URL</h3>
              <form onSubmit={handleUrlSubmit} className="flex gap-2">
                <input
                  type="url"
                  value={recipeUrl}
                  onChange={(e) => setRecipeUrl(e.target.value)}
                  placeholder="Indsæt opskrift URL her"
                  className="flex-grow px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  disabled={loading || !isCurrentDayValid()}
                />
                <button
                  type="submit"
                  disabled={loading || !isCurrentDayValid()}
                  className="flex items-center gap-2 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 disabled:bg-gray-400 disabled:cursor-not-allowed transition-colors"
                >
                  <Link className="w-5 h-5" />
                  {loading ? 'Behandler...' : 'Importér'}
                </button>
              </form>
            </div>

            <div>
              <h3 className="text-lg font-semibold text-gray-900 mb-3">Upload Opskrift Billede</h3>
              <div className="border-2 border-dashed border-gray-300 rounded-lg p-6">
                <label className={`flex flex-col items-center ${loading || !isCurrentDayValid() ? 'cursor-not-allowed' : 'cursor-pointer'}`}>
                  <Upload className="w-8 h-8 text-gray-400 mb-2" />
                  <span className="text-sm text-gray-600">{loading ? 'Behandler...' : 'Klik for at uploade eller træk filen hertil'}</span>
                  <span className="text-xs text-gray-500 mt-1">PNG, JPG op til 10MB</span>
                  <input
                    type="file"
                    onChange={handleFileChange}
                    accept="image/*"
                    className="hidden"
                    disabled={loading || !isCurrentDayValid()}
                  />
                </label>
              </div>
            </div>
          </div>
        </div>

        {/* Total Ingredients List */}
        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 mb-6">
          <h2 className="text-xl font-semibold text-gray-900 mb-6">Samlet Indkøbsliste</h2>
          {['🥩 Kød/Fisk', '🧀 Mejeri', '🥕 Grønt', '🥫 Kolonial', 'Ukategoriseret'].map(category => (
            <IngredientTable key={category} category={category} />
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
