import React, { useState } from 'react';
import { Upload, Link, FileDown, Users } from 'lucide-react';

const FOOD_CATEGORIES = {
  'Kød/Fisk': {
    keywords: [
      'kød', 'bøf', 'bacon', 'laks', 'kotelet', 'reje', 'hummer', 'okse',
      'kylling', 'filet', 'bisk'
    ],
  },
  'Mejeri': {
    keywords: [
      'mælk', 'smør', 'æg', 'cremefraiche', 'fløde', 'yoghurt', 'ost',
      'frossen spinat' // Special case
    ],
  },
  'Kolonial': {
    keywords: [
      // Mel types
      'mel', 'stivelse',
      // Spices and dried herbs
      'stødt', 'tørret', 'krydder',
      // Nuts and seeds (except pine nuts)
      'kerner', 'mandler', 'nødder',
      // Processed items
      'pure', 'hakket tomat', 'plader', 'bouillon',
      // Liquids
      'vin', 'olie',
    ],
    exactMatches: [
      'dijon sennep', 'honning', 'abrikos', 'fladbrød'
    ]
  },
  'Grønt': {
    keywords: [
      // Fresh vegetables
      'kartoffel', 'løg', 'hvidløg', 'salat', 'tomat', 'selleri',
      'champignon', 'squash', 'aubergine', 'gulerod',
      // Fresh herbs (unless marked as dried)
      'persille', 'basilikum', 'dild',
      // Fresh fruits
      'æble', 'citron', 'granatæble',
      // Special cases
      'pinjekerner'
    ],
    excludeIfPresent: ['tørret', 'stødt', 'hakket', 'pure']
  }
};

function App() {
  const [currentDay, setCurrentDay] = useState('Mandag');
  const [loading, setLoading] = useState(false);
  const [recipeUrl, setRecipeUrl] = useState('');
  const [days, setDays] = useState({
    Mandag: { adults: 0, kids: 0, ingredients: [], servings: 0 },
    Tirsdag: { adults: 0, kids: 0, ingredients: [], servings: 0 },
    Onsdag: { adults: 0, kids: 0, ingredients: [], servings: 0 },
    Torsdag: { adults: 0, kids: 0, ingredients: [], servings: 0 },
    Fredag: { adults: 0, kids: 0, ingredients: [], servings: 0 }
  });

  const calculateTotalPeople = (adults, kids) => {
    return adults + (kids / 2);
  };

  const categorizeIngredient = (name) => {
    const normalizedName = name.toLowerCase().trim();

    // Check exact matches first (for special cases)
    for (const [category, data] of Object.entries(FOOD_CATEGORIES)) {
      if (data.exactMatches?.includes(normalizedName)) {
        return category;
      }
    }

    // Check if it's explicitly marked as dried/processed
    const isProcessed = FOOD_CATEGORIES['Grønt'].excludeIfPresent.some(
      word => normalizedName.includes(word)
    );

    // If it contains processed indicators, check Kolonial first
    if (isProcessed) {
      if (FOOD_CATEGORIES['Kolonial'].keywords.some(
        word => normalizedName.includes(word)
      )) {
        return 'Kolonial';
      }
    }

    // Check each category's keywords
    for (const [category, data] of Object.entries(FOOD_CATEGORIES)) {
      if (data.keywords.some(word => normalizedName.includes(word))) {
        return category;
      }
    }

    // If no category found, return uncategorized
    return 'Ukategoriseret';
  };

  const updateIngredientCategory = (ingredientName, newCategory) => {
    setDays(prev => {
      const newDays = { ...prev };
      // Update the ingredient's category across all days
      Object.keys(newDays).forEach(day => {
        newDays[day].ingredients = newDays[day].ingredients.map(ing => {
          if (ing.name === ingredientName) {
            return { ...ing, category: newCategory };
          }
          return ing;
        });
      });
      return newDays;
    });
  };

  // ... rest of your existing functions (handleFileChange, handleUrlSubmit, etc.) ...

  const getCategoryOrder = () => [
    'Kød/Fisk',
    'Mejeri',
    'Grønt',
    'Kolonial',
    'Ukategoriseret'
  ];

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      {/* ... your existing JSX until the ingredients table ... */}

      {/* Ingredients Table */}
      <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 mb-6">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-xl font-semibold text-gray-900">Samlet Indkøbsliste</h2>
        </div>

        <div className="space-y-8">
          {getCategoryOrder().map(category => {
            // Get ingredients for this category
            const categoryIngredients = Object.values(days)
              .flatMap(day => day.ingredients)
              .filter((ingredient, index, self) => 
                index === self.findIndex(i => i.name === ingredient.name) &&
                categorizeIngredient(ingredient.name) === category
              );

            if (categoryIngredients.length === 0) return null;

            return (
              <div key={category} className="space-y-4">
                <h3 className={`text-lg font-semibold ${
                  category === 'Ukategoriseret' ? 'text-amber-600' : 'text-gray-900'
                }`}>
                  {category}
                </h3>
                <table className="w-full">
                  <thead className="bg-gray-50">
                    <tr>
                      <th className="px-4 py-3 text-left text-sm font-semibold text-gray-900">
                        Ingrediens
                        {category === 'Ukategoriseret' && (
                          <span className="block text-xs font-normal text-amber-600">
                            Vælg korrekt kategori
                          </span>
                        )}
                      </th>
                      {Object.keys(days).map(day => (
                        <th key={day} className="px-4 py-3 text-left text-sm font-semibold text-gray-900">
                          {day}<br/>
                          <span className="font-normal text-gray-500">
                            {calculateTotalPeople(days[day].adults, days[day].kids)} personer
                          </span>
                        </th>
                      ))}
                      {category === 'Ukategoriseret' && (
                        <th className="px-4 py-3 text-left text-sm font-semibold text-gray-900">
                          Kategori
                        </th>
                      )}
                    </tr>
                  </thead>
                  <tbody className="divide-y divide-gray-200">
                    {categoryIngredients.map((ingredient) => (
                      <tr key={ingredient.name}>
                        <td className="px-4 py-3 text-sm text-gray-900">
                          {ingredient.name}
                        </td>
                        {Object.entries(days).map(([dayName, dayData]) => {
                          const dayIngredient = dayData.ingredients.find(
                            i => i.name === ingredient.name
                          );

                          if (!dayIngredient) {
                            return (
                              <td key={dayName} className="px-4 py-3 text-sm text-gray-400">
                                -
                              </td>
                            );
                          }

                          const scaled = scaleIngredient(
                            dayIngredient, 
                            dayData.adults, 
                            dayData.kids,
                            dayData.servings
                          );

                          return (
                            <td key={dayName} className="px-4 py-3">
                              <div className="text-sm text-gray-900">
                                {scaled.scaledAmount}
                              </div>
                              <div className="text-xs text-gray-500">
                                (original: {scaled.originalAmount} til {dayData.servings} personer)
                              </div>
                            </td>
                          );
                        })}
                        {category === 'Ukategoriseret' && (
                          <td className="px-4 py-3">
                            <select
                              className="text-sm border rounded p-1"
                              onChange={(e) => updateIngredientCategory(ingredient.name, e.target.value)}
                              defaultValue=""
                            >
                              <option value="" disabled>Vælg kategori</option>
                              <option value="Kød/Fisk">Kød/Fisk</option>
                              <option value="Mejeri">Mejeri</option>
                              <option value="Grønt">Grønt</option>
                              <option value="Kolonial">Kolonial</option>
                            </select>
                          </td>
                        )}
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default App;
