import React, { useState } from 'react';
import { Upload, Link, FileDown, Users } from 'lucide-react';

// Previous constants remain the same...

const App = () => {
  // State definitions remain the same...

  return (
    <div className="min-h-screen bg-zinc-50 py-8">
      <div className="max-w-6xl mx-auto px-4">
        <h1 className="text-3xl font-bold text-zinc-900 mb-8">Opskrift Beregner</h1>

        <div className="bg-white rounded-xl shadow-sm border border-zinc-200 p-6 mb-8">
          <h2 className="text-xl font-semibold text-zinc-900 mb-6">Daglige Opskrifter</h2>

          <div className="flex space-x-2 mb-6 overflow-x-auto pb-2">
            {Object.keys(days).map(day => (
              <button
                key={day}
                onClick={() => setCurrentDay(day)}
                className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                  currentDay === day 
                    ? 'bg-zinc-900 text-white' 
                    : 'bg-white text-zinc-600 hover:bg-zinc-100 border border-zinc-200'
                }`}
              >
                {day}
              </button>
            ))}
          </div>

          <div className="mb-6 p-4 bg-zinc-50 rounded-lg">
            <div className="flex items-center gap-3 mb-4">
              <Users className="text-zinc-700" />
              <span className="text-sm font-medium text-zinc-700">Antal personer for {currentDay}:</span>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              {['servings', 'adults', 'kids'].map(type => (
                <div key={type} className="flex items-center gap-3">
                  <label className="text-sm font-medium text-zinc-600">
                    {type === 'servings' ? 'Original opskrift antal:' : type === 'adults' ? 'Voksne:' : 'Børn:'}
                  </label>
                  <input
                    type="number"
                    min="0"
                    value={days[currentDay][type] || ''}
                    onChange={(e) => type === 'servings' ? updateServings(currentDay, e.target.value) : updatePeople(currentDay, type, e.target.value)}
                    className="w-24 px-3 py-1 border border-zinc-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-zinc-600"
                  />
                </div>
              ))}
            </div>
            <div className="mt-3 text-sm text-zinc-600">
              Samlet antal: {calculateTotalPeople(days[currentDay].adults, days[currentDay].kids)} personer
            </div>
          </div>

          <div className="grid md:grid-cols-2 gap-6">
            <div>
              <h3 className="text-lg font-semibold text-zinc-900 mb-3">Tilføj Opskrift URL</h3>
              <form onSubmit={handleUrlSubmit} className="flex gap-2">
                <input
                  type="url"
                  value={recipeUrl}
                  onChange={(e) => setRecipeUrl(e.target.value)}
                  placeholder="Indsæt opskrift URL her"
                  className="flex-grow px-4 py-2 border border-zinc-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-zinc-600"
                  disabled={loading || !isCurrentDayValid()}
                />
                <button
                  type="submit"
                  disabled={loading || !isCurrentDayValid()}
                  className="flex items-center gap-2 px-4 py-2 bg-zinc-900 text-white rounded-lg hover:bg-zinc-800 disabled:bg-zinc-400 disabled:cursor-not-allowed transition-colors"
                >
                  <Link className="w-5 h-5" />
                  {loading ? 'Behandler...' : 'Importér'}
                </button>
              </form>
            </div>

            <div>
              <h3 className="text-lg font-semibold text-zinc-900 mb-3">Upload Opskrift Billede</h3>
              <div className="border-2 border-dashed border-zinc-300 rounded-lg p-6">
                <label className={`flex flex-col items-center ${loading || !isCurrentDayValid() ? 'cursor-not-allowed' : 'cursor-pointer'}`}>
                  <Upload className="w-8 h-8 text-zinc-400 mb-2" />
                  <span className="text-sm text-zinc-600">{loading ? 'Behandler...' : 'Klik for at uploade eller træk filen hertil'}</span>
                  <span className="text-xs text-zinc-500 mt-1">PNG, JPG op til 10MB</span>
                  <input
                    type="file"
                    onChange={handleFileChange}
                    accept="image/*"
                    className="hidden"
                    disabled={loading || !isCurrentDayValid()}
                  />
                </label>
              </div>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-xl shadow-sm border border-zinc-200 p-6 mb-6">
          <h2 className="text-xl font-semibold text-zinc-900 mb-6">Samlet Indkøbsliste</h2>
          {['🥩 Kød/Fisk', '🧀 Mejeri', '🥕 Grønt', '🥫 Kolonial', 'Ukategoriseret'].map(category => (
            <IngredientTable key={category} category={category} />
          ))}
        </div>
      </div>
    </div>
  );
}

// Update IngredientTable component colors
const IngredientTable = ({ category }) => {
  // ... (previous logic remains the same)

  return (
    <div key={category} className="mb-8">
      <h3 className={`text-lg font-semibold mb-4 ${category === 'Ukategoriseret' ? 'text-amber-600' : 'text-zinc-900'}`}>
        {category}
      </h3>
      <table className="w-full">
        <thead className="bg-zinc-50">
          <tr>
            <th className="px-4 py-3 text-left text-sm font-semibold text-zinc-900">Ingrediens</th>
            {Object.keys(days).map(day => (
              <th key={day} className="px-4 py-3 text-left text-sm font-semibold text-zinc-900">
                {day}<br/>
                <span className="font-normal text-zinc-500">
                  {calculateTotalPeople(days[day].adults, days[day].kids)} personer
                </span>
              </th>
            ))}
          </tr>
        </thead>
        <tbody className="divide-y divide-zinc-200">
          {categoryIngredients.map(ingredient => (
            <tr key={ingredient.name}>
              <td className="px-4 py-3 text-sm text-zinc-900">{ingredient.name} ({ingredient.occurrences} times)</td>
              {Object.entries(days).map(([dayName, dayData]) => {
                const dayIngredient = dayData.ingredients.find(i => i.name === ingredient.name);
                if (!dayIngredient) return <td key={dayName} className="px-4 py-3 text-sm text-zinc-400">-</td>;
                const scaled = scaleIngredient(dayIngredient, dayData.adults, dayData.kids, dayData.servings);
                return (
                  <td key={dayName} className="px-4 py-3">
                    <div className="text-sm text-zinc-900">{scaled.scaledAmount}</div>
                    <div className="text-xs text-zinc-500">(original: {scaled.originalAmount} til {dayData.servings} personer)</div>
                  </td>
                );
              })}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default App;
