import React, { useState } from 'react';
import { Upload, Link, FileDown, Users } from 'lucide-react';

const App = () => {
  const [currentDay, setCurrentDay] = useState('Mandag');
  const [loading, setLoading] = useState(false);
  const [recipeUrl, setRecipeUrl] = useState('');
  const [days, setDays] = useState(createInitialDays());

  const handleFileChange = async (event) => {
    const file = event.target.files[0];
    if (!file || !file.type.startsWith('image/')) {
      alert('Venligst v√¶lg en billedfil (PNG, JPG)');
      return;
    }
    setLoading(true);
    try {
      const formData = new FormData();
      formData.append('file', file);
      const response = await fetch('http://localhost:8000/parse-image', {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();
      if (data.success) {
        updateIngredients(data.ingredients);
      } else {
        throw new Error(data.error || 'Failed to process image');
      }
    } catch (error) {
      alert('Der opstod en fejl under behandling af filen');
    } finally {
      setLoading(false);
    }
  };

  const handleUrlSubmit = async (e) => {
    e.preventDefault();
    if (!recipeUrl.trim()) return;
    setLoading(true);
    try {
      const response = await fetch(`http://localhost:8000/parse-url?url=${encodeURIComponent(recipeUrl)}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
      });
      const data = await response.json();
      if (data.success) {
        updateIngredients(data.ingredients);
        setRecipeUrl('');
      } else {
        throw new Error(data.error || 'Failed to process URL');
      }
    } catch (error) {
      alert('Der opstod en fejl under behandling af URLen');
    } finally {
      setLoading(false);
    }
  };

  const updateIngredients = (ingredients) => {
    const normalizedIngredients = ingredients.map(ingredient => ({
      ...ingredient,
      name: normalizeIngredient(ingredient.name),
    }));

    setDays(prev => ({
      ...prev,
      [currentDay]: { ...prev[currentDay], ingredients: normalizedIngredients }
    }));
  };

  const updatePeople = (day, type, value) => {
    setDays(prev => ({
      ...prev,
      [day]: { ...prev[day], [type]: parseInt(value) || 0 }
    }));
  };

  const updateServings = (day, value) => {
    setDays(prev => ({
      ...prev,
      [day]: { ...prev[day], servings: parseInt(value) || 0 }
    }));
  };

  const scaleIngredient = (ingredient, adults, kids, originalServings) => {
    const targetServings = adults + (kids / 2);
    const scalingFactor = targetServings / originalServings;
    const originalAmount = parseFloat(ingredient.amount);
    const scaledAmount = (originalAmount * scalingFactor).toFixed(1);
    return { ...ingredient, originalAmount: `${ingredient.amount}${ingredient.unit}`, scaledAmount: `${scaledAmount}${ingredient.unit}` };
  };

  const calculateTotalPeople = (adults, kids) => adults + (kids / 2);

  const isCurrentDayValid = () => {
    const { servings, adults, kids } = days[currentDay];
    return servings > 0 && (adults > 0 || kids > 0);
  };

  const IngredientTable = ({ category }) => {
    const categorizeIngredient = (name) => {
      const normalizedName = normalizeIngredient(name);
      return Object.entries(FOOD_CATEGORIES).find(([_, keywords]) =>
        keywords.some(word => normalizedName.includes(word))
      )?.[0] || 'Ukategoriseret';
    };

    const groupedIngredients = Object.values(days)
      .flatMap(day => day.ingredients)
      .reduce((acc, ingredient) => {
        const normName = normalizeIngredient(ingredient.name);
        if (!acc[normName]) {
          acc[normName] = { ...ingredient, occurrences: 1 };
        } else {
          acc[normName].occurrences += 1;
        }
        return acc;
      }, {});

    const categoryIngredients = Object.values(groupedIngredients)
      .filter((ingredient) => categorizeIngredient(ingredient.name) === category);

    if (categoryIngredients.length === 0) return null;

    return (
      <div key={category} className="mb-8">
        <h3 className={`text-lg font-semibold mb-4 ${category === 'Ukategoriseret' ? 'text-yellow-600' : 'text-gray-800'}`}>
          {category}
        </h3>
        <table className="w-full table-auto">
          <thead className="bg-gray-100">
            <tr>
              <th className="px-4 py-3 text-left text-sm font-semibold text-gray-800">Ingrediens</th>
              {Object.keys(days).map(day => (
                <th key={day} className="px-4 py-3 text-left text-sm font-semibold text-gray-800">
                  {day}<br />
                  <span className="font-normal text-gray-500">
                    {calculateTotalPeople(days[day].adults, days[day].kids)} personer
                  </span>
                </th>
              ))}
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-200">
            {categoryIngredients.map(ingredient => (
              <tr key={ingredient.name}>
                <td className="px-4 py-3 text-sm text-gray-800">{ingredient.name} ({ingredient.occurrences} times)</td>
                {Object.entries(days).map(([dayName, dayData]) => {
                  const dayIngredient = dayData.ingredients.find(i => i.name === ingredient.name);
                  if (!dayIngredient) return <td key={dayName} className="px-4 py-3 text-sm text-gray-400">-</td>;
                  const scaled = scaleIngredient(dayIngredient, dayData.adults, dayData.kids, dayData.servings);
                  return (
                    <td key={dayName} className="px-4 py-3">
                      <div className="text-sm text-gray-800">{scaled.scaledAmount}</div>
                      <div className="text-xs text-gray-500">(original: {scaled.originalAmount} til {dayData.servings} personer)</div>
                    </td>
                  );
                })}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-6xl mx-auto px-6">
        <h1 className="text-4xl font-bold text-gray-800 mb-8">Opskrift Beregner</h1>

        <div className="bg-white rounded-xl shadow-lg p-8 mb-8">
          <h2 className="text-2xl font-semibold text-gray-800 mb-6">Daglige Opskrifter</h2>

          <div className="flex space-x-3 mb-6 overflow-x-auto pb-2">
            {Object.keys(days).map(day => (
              <button
                key={day}
                onClick={() => setCurrentDay(day)}
                className={`px-6 py-3 rounded-full font-medium transition-all ${currentDay === day ? 'bg-blue-600 text-white' : 'bg-gray-100 text-gray-600 hover:bg-gray-200'}`}
              >
                {day}
              </button>
            ))}
          </div>

          {/* People and Servings Inputs */}
          <div className="mb-6 p-6 bg-blue-50 rounded-lg">
            <div className="flex items-center gap-4 mb-4">
              <Users className="text-blue-500" />
              <span className="text-sm font-medium text-gray-700">Antal personer for {currentDay}:</span>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {['servings', 'adults', 'kids'].map(type => (
                <div key={type} className="flex items-center gap-4">
                  <label className="text-sm font-medium text-gray-600">{type === 'servings' ? 'Original opskrift antal:' : type === 'adults' ? 'Voksne:' : 'B√∏rn:'}</label>
                  <input
                    type="number"
                    min="0"
                    value={days[currentDay][type] || ''}
                    onChange={(e) => type === 'servings' ? updateServings(currentDay, e.target.value) : updatePeople(currentDay, type, e.target.value)}
                    className="w-32 px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>
              ))}
            </div>
            <div className="mt-3 text-sm text-gray-600">
              Samlet antal: {calculateTotalPeople(days[currentDay].adults, days[currentDay].kids)} personer
            </div>
          </div>

          {/* Recipe Input Methods */}
          <div className="grid md:grid-cols-2 gap-8">
            <div>
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Tilf√∏j Opskrift URL</h3>
              <form onSubmit={handleUrlSubmit} className="flex gap-4">
                <input
                  type="url"
                  value={recipeUrl}
                  onChange={(e) => setRecipeUrl(e.target.value)}
                  placeholder="Inds√¶t opskrift URL her"
                  className="flex-grow px-6 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  disabled={loading || !isCurrentDayValid()}
                />
                <button
                  type="submit"
                  disabled={loading || !isCurrentDayValid()}
                  className="px-6 py-3 bg-blue-600 text-white rounded-full hover:bg-blue-700 disabled:bg-gray-400 disabled:cursor-not-allowed transition-colors"
                >
                  <Link className="w-5 h-5" />
                  {loading ? 'Behandler...' : 'Import√©r'}
                </button>
              </form>
            </div>

            <div>
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Upload Opskrift Billede</h3>
              <div className="border-2 border-dashed border-gray-300 rounded-lg p-6">
                <label className={`flex flex-col items-center ${loading || !isCurrentDayValid() ? 'cursor-not-allowed' : 'cursor-pointer'}`}>
                  <Upload className="w-8 h-8 text-gray-400 mb-3" />
                  <span className="text-sm text-gray-600">{loading ? 'Behandler...' : 'Klik for at uploade eller tr√¶k filen hertil'}</span>
                  <span className="text-xs text-gray-500 mt-2">PNG, JPG op til 10MB</span>
                  <input
                    type="file"
                    onChange={handleFileChange}
                    accept="image/*"
                    className="hidden"
                    disabled={loading || !isCurrentDayValid()}
                  />
                </label>
              </div>
            </div>
          </div>
        </div>

        {/* Total Ingredients List */}
        <div className="bg-white rounded-xl shadow-lg p-8">
          <h2 className="text-2xl font-semibold text-gray-800 mb-6">Samlet Indk√∏bsliste</h2>
          {['ü•© K√∏d/Fisk', 'üßÄ Mejeri', 'ü•ï Gr√∏nt', 'ü•´ Kolonial', 'Ukategoriseret'].map(category => (
            <IngredientTable key={category} category={category} />
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
